mapper.select:
SELECT article.*,`member`.nickname AS extra_WriterName FROM article LEFT JOIN `member` ON article.memberId = `member`.id WHERE 1
AND article.boardId = #{boardId}
ORDER BY id DESC
LIMIT #{limitStart}, #{limitTake}

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}
